<?php
class ap_import extends WP_Import
{
	function recursive_array_replace($find, $replace, $array){
		$newArray = array();
		foreach ($array as $key => $value) {
		
		if (!is_array($value)) {
		$newArray[$key] = str_replace($find, $replace, $value);
		}else{
		$newArray[$key] = $this->recursive_array_replace($find, $replace, $value);
		}
		}
		return $newArray;
	}

    function set_theme_option(){

  	$import_code = "";
    $import_code = base64_decode($import_code);
    $import_code = unserialize($import_code);
    $import_code = $this->recursive_array_replace('https://accesspressthemes.com/accesspress-parallax-pro-demo', get_site_url() , $import_code );
    update_option('accesspress_parallax_pro', $import_code);

    $import_code1 = 'YToxMzp7aTowO2k6MjY7aToxO2k6MzM7aToyO2k6ODtpOjM7aTo3MTtpOjQ7aTo2NDtpOjU7aToxODtpOjY7aTo2ODtpOjc7aTozNTtpOjg7aTo2MDtpOjk7aTozO2k6MTA7aToxO2k6MTE7aTo1O2k6MTI7aTo1ODt9';
    $import_code1 = base64_decode($import_code1);
    $import_code1 = unserialize($import_code1);
    update_option('accesspress_parallax_count', $import_code1);
    }

    function set_menu() {	
		global $wpdb;
		$table_db_name = $wpdb->prefix . "terms";
		$rows = $wpdb->get_results("SELECT * FROM $table_db_name WHERE name='Top Menu' OR name='Main Menu'",ARRAY_A);
        $menu_ids = array();
		foreach($rows as $row) {
			$menu_ids[$row["name"]] = $row["term_id"] ;
		}
		set_theme_mod( 'nav_menu_locations', array_map( 'absint', array( 'topmenu' =>$menu_ids['Top Menu'], 'primary' =>$menu_ids['Main Menu'] ) ) );			
	}

	function set_widgets() {
		$sidebars_widgets_code = '';
		$sidebars_widgets_code = base64_decode($sidebars_widgets_code);
	    $sidebars_widgets_code = unserialize($sidebars_widgets_code);
	    update_option('sidebars_widgets', $sidebars_widgets_code);

	    $all_widgets_code = '';
	    $all_widgets_code = base64_decode($all_widgets_code);
	    $all_widgets_code = unserialize($all_widgets_code);
	    
		foreach ($all_widgets_code as $option) {
			$option_array = unserialize($option->option_value);
			$option_array = $this->recursive_array_replace('https://accesspressthemes.com/accesspress-parallax-pro-demo', get_site_url() , $option_array );
			update_option($option->option_name, $option_array);
		}
	}
}